mapper.select:
SELECT site_id,network,ip_type,update_time 
FROM dhcp_network

	WHERE union_table.site_id = #{site_id}

SELECT count(*) AS total
FROM dhcp_network

	WHERE union_table.site_id = #{site_id}

SELECT union_table.group_id, union_table.group_name,union_table."key", union_table."type",union_table.site_id,union_table.ip_type FROM ( SELECT cg.group_id,COALESCE(cg.group_name,'') AS group_name, dn."network" AS key, COALESCE(cg."type",'N') AS "type",COALESCE(cg.site_id,dn.site_id) as site_id,dn.ip_type FROM dhcp_network AS dn LEFT JOIN custom_group AS cg ON dn."network" = cg.key and cg."type" = 'N' WHERE cg.group_id is null AND (dn."network" like CONCAT('%', #{searchValue}, '%') OR cg.group_name like CONCAT('%', #{searchValue}, '%')) AND dn.ip_type=#{ip_type} UNION SELECT group_id,COALESCE(group_name,'') AS group_name, "key", "type",site_id,ip_type FROM custom_group WHERE "type" = 'N' AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%')) AND ip_type=#{ip_type} ) as union_table
WHERE union_table.site_id = #{site_id}
ORDER BY
inet(union_table.key) union_table.${orderColumn} ${orderType} OFFSET #{startIndex} LIMIT #{length}

SELECT group_id,COALESCE(group_name,'') AS group_name, "key", "type",site_id,ip_type FROM custom_group WHERE "type" = 'N'
AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%'))
AND union_table.site_id = #{site_id}

SELECT count(*) AS total FROM custom_group WHERE "type" = 'N'
AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%'))
AND union_table.site_id = #{site_id}

mapper.update:
UPDATE custom_group 
SET group_name=#{group_name} 
WHERE group_id=#{group_id}

mapper.select:
SELECT key,group_name,ip_type,site_id
FROM custom_group

	WHERE site_id = #{site_id}

SELECT DISTINCT device_type 
FROM view_client_info 
ORDER BY device_type ;

SELECT macaddr, duid,hostname,vendor,model,COALESCE(os,'nomatch')as os,device_type,category,ip_type,last_ip,last_ip_num ,last_switch,last_port,position_update,user_comment,update_time FROM view_client_info
WHERE last_ip_num >= #{start_ip_num} AND last_ip_num <= #{end_ip_num}
AND ip_type=#{ip_type} AND ( last_ip like CONCAT('%', #{searchValue}, '%') OR macaddr like CONCAT('%', #{searchValue}, '%') OR hostname like CONCAT('%', #{searchValue}, '%') OR os like CONCAT('%', #{searchValue}, '%')
OR os is null OR os is null OR os is null OR os is null OR os is null OR os is null OR os is null
)
AND device_type='' AND device_type=#{device_type}
ORDER BY
inet(${orderColumn}) ${orderColumn}
${orderType} OFFSET #{startIndex} LIMIT #{length}

SELECT count(*) AS total FROM view_client_info
WHERE last_ip_num >= #{start_ip_num} AND last_ip_num <= #{end_ip_num}
AND ip_type=#{ip_type} AND ( last_ip like CONCAT('%', #{searchValue}, '%') OR macaddr like CONCAT('%', #{searchValue}, '%') OR hostname like CONCAT('%', #{searchValue}, '%') OR os like CONCAT('%', #{searchValue}, '%')
OR os is null OR os is null OR os is null OR os is null OR os is null OR os is null OR os is null
)
AND device_type='' AND device_type=#{device_type}

SELECT client_status.*,si.site_name 
FROM (
	SELECT vci."macaddr",vci.duid,vci.hostname,vci.vendor,vci.model,vis.os
	,vci.device_type,vci.category,vci.last_ip as ipaddr,vci.last_ip_num as ip_num,vci.last_switch
	,vci.last_port,vis.site_id,vis.is_range,vis.ip_status
	FROM view_client_info AS vci LEFT OUTER JOIN view_ip_status AS vis 
	ON vis.ipaddr = vci.last_ip AND (vis.macaddr = vci.macaddr OR vis.duid = vci.duid) 
	WHERE vci."macaddr"=#{macaddr} AND vci.last_ip=#{ipaddr}
) AS client_status LEFT OUTER JOIN site_info AS si 
ON client_status.site_id = si.site_id;
