mapper.select:
SELECT site_id,network,ip_type,update_time 
FROM dhcp_network

	WHERE union_table.site_id = #{site_id}

SELECT count(*) AS total
FROM dhcp_network

	WHERE union_table.site_id = #{site_id}

SELECT union_table.group_id, union_table.group_name,union_table."key", union_table."type",union_table.site_id,union_table.ip_type FROM ( SELECT cg.group_id,COALESCE(cg.group_name,'') AS group_name, dn."network" AS key, COALESCE(cg."type",'N') AS "type",COALESCE(cg.site_id,dn.site_id) as site_id,dn.ip_type FROM dhcp_network AS dn LEFT JOIN custom_group AS cg ON dn."network" = cg.key and cg."type" = 'N' WHERE cg.group_id is null AND (dn."network" like CONCAT('%', #{searchValue}, '%') OR cg.group_name like CONCAT('%', #{searchValue}, '%')) AND dn.ip_type=#{ip_type} UNION SELECT group_id,COALESCE(group_name,'') AS group_name, "key", "type",site_id,ip_type FROM custom_group WHERE "type" = 'N' AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%')) AND ip_type=#{ip_type} ) as union_table
WHERE union_table.site_id = #{site_id}
ORDER BY
inet(union_table.key) union_table.${orderColumn} ${orderType} OFFSET #{startIndex} LIMIT #{length}

SELECT group_id,COALESCE(group_name,'') AS group_name, "key", "type",site_id,ip_type FROM custom_group WHERE "type" = 'N'
AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%'))
AND union_table.site_id = #{site_id}

SELECT count(*) AS total FROM custom_group WHERE "type" = 'N'
AND (key like CONCAT('%', #{searchValue}, '%') OR group_name like CONCAT('%', #{searchValue}, '%'))
AND union_table.site_id = #{site_id}

mapper.update:
UPDATE custom_group 
SET group_name=#{group_name} 
WHERE group_id=#{group_id}
