mapper.select:
		SELECT * 
		  FROM "view_network_ip_status", (SELECT count(*) allcount
                                  FROM "view_network_ip_status") as allcount
		                         WHERE site_id = ${siteid}
		                           AND (network like CONCAT('%', #{searchValue}, '%') OR comment like CONCAT('%', #{searchValue}, '%')
		                            OR start_ip like CONCAT('%', #{searchValue}, '%') OR end_ip like CONCAT('%', #{searchValue}, '%'))
		 ORDER BY
		
			 inet(${orderColumn}) ${orderType} 
			 ${orderColumn} ${orderType} 
		
		OFFSET #{startIndex} LIMIT #{length};

SET TIMEZONE TO '${time_zone}'; SELECT * FROM (SELECT "statusView"."network", "statusView"."ipaddr", COALESCE("statusView"."ip_type",'') AS "ip_type", COALESCE("statusView"."macaddr",'') AS "macaddr", COALESCE("statusView"."duid",'') AS "duid", COALESCE("statusView"."ip_status",'') AS "ip_status", COALESCE("statusView"."host_name",'') AS "host_name", COALESCE("statusTable"."host_os",'') AS "host_os", COALESCE("statusView"."fingerprint",'') AS "fingerprint", COALESCE(to_char("statusView"."lease_start_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "lease_start_time", COALESCE(to_char("statusView"."lease_end_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "lease_end_time", COALESCE("statusTable"."user_description",'') AS "user_description" FROM "view_ip_status" as "statusView", "dhcp_ip_status" AS "statusTable" WHERE "statusView"."site_id" = "statusTable"."site_id" AND "statusView"."ipaddr" = "statusTable"."ipaddr" and "statusView"."site_id" = #{siteid} AND "statusView"."network" = #{network} AND ("statusView"."network" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ipaddr" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ip_type" like CONCAT('%', #{searchValue}, '%') OR "statusView"."macaddr" like CONCAT('%', #{searchValue}, '%') OR "statusView"."duid" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ip_status" like CONCAT('%', #{searchValue}, '%') OR "statusView"."host_name" like CONCAT('%', #{searchValue}, '%') OR "statusTable"."host_os" like CONCAT('%', #{searchValue}, '%') OR "statusView"."fingerprint" like CONCAT('%', #{searchValue}, '%') OR "statusTable"."user_description" like CONCAT('%', #{searchValue}, '%') )) AS mainTable, (SELECT COUNT ("statusView"."network") AS "allcount" FROM "view_ip_status" as "statusView", "dhcp_ip_status" AS "statusTable" WHERE "statusView"."site_id" = "statusTable"."site_id" AND "statusView"."ipaddr" = "statusTable"."ipaddr" and "statusView"."site_id" = #{siteid} AND "statusView"."network" = #{network} AND ("statusView"."network" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ipaddr" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ip_type" like CONCAT('%', #{searchValue}, '%') OR "statusView"."macaddr" like CONCAT('%', #{searchValue}, '%') OR "statusView"."duid" like CONCAT('%', #{searchValue}, '%') OR "statusView"."ip_status" like CONCAT('%', #{searchValue}, '%') OR "statusView"."host_name" like CONCAT('%', #{searchValue}, '%') OR "statusTable"."host_os" like CONCAT('%', #{searchValue}, '%') OR "statusView"."fingerprint" like CONCAT('%', #{searchValue}, '%') OR "statusTable"."user_description" like CONCAT('%', #{searchValue}, '%') )) as countTable ORDER BY
