		SET TIMEZONE TO '${time_zone}';
		
		CREATE OR REPLACE FUNCTION fnRowCount(int,text,text,text,text) RETURNS int LANGUAGE plpgsql AS $$
		DECLARE
		    V1 ALIAS FOR $1;
		    V2 ALIAS FOR $2;
		    V3 ALIAS FOR $3; 
		    V4 ALIAS FOR $4;
		    V5 ALIAS FOR $5;
		    rowCnt int;
		BEGIN
		    rowCnt := Count(*)
		      FROM "dhcp_ip_status"
		     WHERE "site_id" = V1		   
		
			 AND "network" = V2 
				
		       AND "is_conflict" != true
		       AND "usage" = V3
		       AND "obj_types" like CONCAT('%'||V4||'%')
		       AND ("ipaddr" like CONCAT('%'||V5||'%') OR "macaddr" like CONCAT('%'||V5||'%')
		   	    OR "host_name" like CONCAT('%'||V5||'%'));
		    RETURN rowCnt;
		END
		$$;
		--SELECT fnRowCount('192.168.1.0/24', 'DHCP', 'LEASE');

		SELECT (ROW_NUMBER() OVER()) AS "rownum", "ipaddr", "macaddr", "host_name", "host_os", 
			   COALESCE("duid", '') AS "duid", "status",
			   "lease_state", "obj_types", "discover_status", "usage", "fingerprint",
               COALESCE("is_never_ends", FALSE) AS "is_never_ends", COALESCE("is_never_start", FALSE) AS "is_never_start",			   
			   COALESCE(to_char("lease_start_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "lease_start_time",
			   COALESCE(to_char("lease_end_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "lease_end_time",
               COALESCE(to_char("last_discovered",'YYYY-MM-DD HH24:MI:SS'),'') AS "last_discovered",
               COALESCE(to_char("update_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "update_time",
		       COALESCE("user_description", '') AS "user_description", fnRowCount(#{siteid}, #{network}, 'DHCP', 'LEASE', #{searchValue}) AS "count"
		  FROM "dhcp_ip_status"
		 WHERE "site_id" = #{siteid}
		
			 AND "network" = #{network} 
		
		   AND "is_conflict" != true
		   AND "usage" = 'DHCP'
		   AND "obj_types" like CONCAT('%', 'LEASE', '%')
		   AND ("ipaddr" like CONCAT('%', #{searchValue}, '%') OR "macaddr" like CONCAT('%', #{searchValue}, '%')
		   	    OR "host_name" like CONCAT('%', #{searchValue}, '%'))
		 ORDER BY
		
			 inet(${orderColumn}) ${orderType} 
			 ${orderColumn} ${orderType} 
		
		OFFSET #{startIndex} LIMIT #{length};
		DROP function fnRowCount(int,text,text,text,text);

SET TIMEZONE TO '${time_zone}'; SELECT "apply"."user_id", "apply"."user_site_id", "usersite"."site_name", COALESCE("apply"."user_name", '') AS "user_name", COALESCE("apply"."user_phone_num", '') AS "user_phone_num", "apply"."apply_site_id", "applysite"."site_name", "apply"."apply_static_ip_type", COALESCE("apply"."apply_static_ipaddr", '') AS "apply_static_ipaddr", COALESCE("apply"."apply_static_ip_num", '0') AS "apply_static_ip_num", COALESCE(to_char("apply"."apply_start_time",'YYYY-MM-DD'),'') || ' ~ ' || COALESCE(to_char("apply"."apply_end_time",'YYYY-MM-DD'),'') AS "apply_use_time", COALESCE("apply"."apply_description", '') AS "apply_description", COALESCE(to_char("apply"."apply_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "apply_time", COALESCE("apply"."settlement_status", '0') AS "settlement_status", COALESCE("apply"."settlement_chief_id", '') AS "settlement_chief_id", COALESCE("apply"."settlement_chief_name", '') AS "settlement_chief_name", COALESCE("apply"."settlement_description", '') AS "settlement_description", COALESCE(to_char("apply"."settlement_time",'YYYY-MM-DD HH24:MI:SS'),'') AS "settlement_time", COALESCE("apply"."issuance_ip_type", '') AS "issuance_ip_type", COALESCE("apply"."issuance_ipaddr", '') AS "issuance_ipaddr", COALESCE("apply"."issuance_ip_num", '0') AS "issuance_ip_num", COALESCE(to_char("apply"."issuance_start_time",'YYYY-MM-DD'),'') || ' ~ ' || COALESCE(to_char("apply"."issuance_end_time",'YYYY-MM-DD'),'') AS "issuance_use_time", "rowCount"."allCount" FROM "user_apply_ip_info" AS "apply", "site_info" AS "usersite", "site_info" AS "applysite", (SELECT count(*) AS "allCount" FROM "user_apply_ip_info" AS "apply", "site_info" AS "usersite", "site_info" AS "applysite" WHERE "apply"."user_site_id" = "usersite"."site_id" AND "apply"."apply_site_id" = "applysite"."site_id"
AND "apply"."apply_site_id" = #{siteid}
AND "apply"."settlement_status" = #{settlementstatus}
AND ("usersite"."site_name" like CONCAT('%', #{searchValue}, '%') OR "applysite"."site_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."user_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."user_phone_num" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_static_ip_type" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_static_ipaddr" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_description" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_chief_id" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_chief_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_description" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ip_type" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ipaddr" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ip_type" like CONCAT('%', #{searchValue}, '%')) AND "apply"."settlement_status" = #{settlementstatus} AND ("apply"."apply_time" <= timestamp '${endtime}' AND "apply"."apply_time" >= timestamp '${starttime}')) AS "rowCount" WHERE "apply"."user_site_id" = "usersite"."site_id" AND "apply"."apply_site_id" = "applysite"."site_id"
AND "apply"."apply_site_id" = #{siteid}
AND "apply"."settlement_status" = #{settlementstatus}
AND ("usersite"."site_name" like CONCAT('%', #{searchValue}, '%') OR "applysite"."site_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."user_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."user_phone_num" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_static_ip_type" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_static_ipaddr" like CONCAT('%', #{searchValue}, '%') OR "apply"."apply_description" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_chief_id" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_chief_name" like CONCAT('%', #{searchValue}, '%') OR "apply"."settlement_description" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ip_type" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ipaddr" like CONCAT('%', #{searchValue}, '%') OR "apply"."issuance_ip_type" like CONCAT('%', #{searchValue}, '%')) AND ("apply"."apply_time" <= timestamp '${endtime}' AND "apply"."apply_time" >= timestamp '${starttime}')
