mapper.update:
SET TIMEZONE TO '${time_zone}';
UPDATE system_user_info set
user_pw=#{user_pw},
user_name=#{user_name},group_id=#{group_id},site_id=#{site_id},dept_name=#{dept_name},position_name=#{position_name},email=#{email},phone_num=#{phone_num},mobile_num=#{mobile_num},modify_date=now()
WHERE user_id=#{user_id};

mapper.select:
SELECT * FROM "test_dhcp_segment" ,(SELECT count(*) allcount FROM "test_dhcp_segment") as allcount
 WHERE network like CONCAT('%', #{searchValue}, '%') OR comment like CONCAT('%', #{searchValue}, '%') OR site like CONCAT('%', #{searchValue}, '%')
 ORDER BY ${orderColumn} ${orderType}
OFFSET #{startIndex} LIMIT #{length};

SELECT * FROM "test_dhcp_segment_list",(SELECT count(*) allcount FROM "test_dhcp_segment_list") as allcount
 WHERE segment_id = ${segmentid}
   AND "ip" like CONCAT('%', #{searchValue}, '%') OR "name" like CONCAT('%', #{searchValue}, '%') OR "mac" like CONCAT('%', #{searchValue}, '%')
    OR "status" like CONCAT('%', #{searchValue}, '%') OR "type" like CONCAT('%', #{searchValue}, '%') OR "client" like CONCAT('%', #{searchValue}, '%')
 ORDER BY ${orderColumn} ${orderType}
OFFSET #{startIndex} LIMIT #{length};
