mapper.update:

	UPDATE dhcp_network
	SET start_ip=#{start_ip}, end_ip=#{end_ip}, comment=#{comment}, update_time=now()
	WHERE site_id=#{site_id} AND network =#{network}


	UPDATE dhcp_range
	SET end_ip=#{end_ip}, update_time=now()
	WHERE site_id=#{site_id} AND network =#{network} AND start_ip = #{start_ip}


	UPDATE dhcp_mac_filter
	SET update_time=now()
	WHERE site_id=#{site_id} AND filter_name=#{filter_name}

		
			UPDATE dhcp_lease_ip
			SET network=#{network}, ip_type=#{ip_type}, macaddr=#{macaddr}, duid=#{duid}, hostname=#{hostname}, state=#{state} 
       			, username=#{username}, fingerprint=#{fingerprint}, os=#{os}
       			, lease_start_time=#{lease_start_time}, lease_end_time=#{lease_end_time}, last_discovered=#{last_discovered}
       			, update_time=now()
			WHERE site_id=#{site_id} AND ipaddr=#{ipaddr}


	UPDATE dhcp_fixed_ip
	SET network=#{network}, macaddr=#{macaddr}, comment=#{comment}, disable=#{disable}, update_time=now()
	WHERE site_id=#{site_id} AND ipaddr=#{ipaddr}
