mapper.update:

	UPDATE dhcp_network
	SET start_ip=#{start_ip}, end_ip=#{end_ip}, comment=#{comment}, update_time=now()
	WHERE site_id=#{site_id} AND network =#{network}


	UPDATE dhcp_range
	SET end_ip=#{end_ip}, comment=#{comment}, update_time=now()
	WHERE site_id=#{site_id} AND network =#{network} AND start_ip = #{start_ip}
